#-------K·∫øt n·ªëi mysql
def get_connection():
    try:
        #h√†m ƒë·ªÉ tr·∫£ v·ªÅ database k·∫øt n·ªëi
        conn = pymssql.connect(
            server=SQL_SERVER_CONFIG['server'],
            port=int(SQL_SERVER_CONFIG['port']),
            user=SQL_SERVER_CONFIG['username'],
            password=SQL_SERVER_CONFIG['password'],
            database=SQL_SERVER_CONFIG['database']
        )
        return conn
    except pymssql.Error as e:
        print("L·ªói k·∫øt n·ªëi v·ªõi mysql")
        return None

# ===== SQL Event functions =====
def add_book():
    try:
        conn = get_connection()
        cursor = conn.cursor()
        sql = """INSERT INTO Books (BookID, CopyID, Publisher, Status, StorageNote, Barcode, Price)
                 VALUES (?, ?, ?, ?, ?, ?, ?)"""
        cursor.execute(sql, (
            entry_8.get(), entry_7.get(), entry_5.get(),
            entry_1.get(), entry_3.get(), entry_4.get(), entry_2.get()
        ))
        conn.commit()
        cursor.close()
        conn.close()
        messagebox.showinfo("Success", "Book added successfully!")
    except Exception as e:
        messagebox.showerror("Error", str(e))

def update_book():
    try:
        conn = get_connection()
        cursor = conn.cursor()
        sql = "UPDATE Books SET Status=?, Price=? WHERE BookID=?"
        cursor.execute(sql, (entry_1.get(), entry_2.get(), entry_8.get()))
        conn.commit()
        cursor.close()
        conn.close()
        messagebox.showinfo("Success", "Book updated successfully!")
    except Exception as e:
        messagebox.showerror("Error", str(e))

def delete_book():
    try:
        conn = get_connection()
        cursor = conn.cursor()
        sql = "DELETE FROM Books WHERE BookID=?"
        cursor.execute(sql, (entry_8.get(),))
        conn.commit()
        cursor.close()
        conn.close()
        messagebox.showinfo("Success", "Book deleted successfully!")
    except Exception as e:
        messagebox.showerror("Error", str(e))

