def add_reader():
    try:
        conn = get_connection()
        cursor = conn.cursor()
        sql = """INSERT INTO Readers (ReaderID, FullName, Phone, Address)
                 VALUES (?, ?, ?, ?)"""
        cursor.execute(sql, (
            entry_8.get(), entry_2.get(), entry_3.get(), entry_4.get()
        ))
        conn.commit()
        cursor.close()
        conn.close()
        messagebox.showinfo("Success", "Reader added successfully!")
    except Exception as e:
        messagebox.showerror("Error", str(e))

def update_reader():
    try:
        conn = get_connection()
        cursor = conn.cursor()
        sql = """UPDATE Readers SET FullName=?, Phone=?, Address=? WHERE ReaderID=?"""
        cursor.execute(sql, (entry_2.get(), entry_3.get(), entry_4.get(), entry_8.get()))
        conn.commit()
        cursor.close()
        conn.close()
        messagebox.showinfo("Success", "Reader updated successfully!")
    except Exception as e:
        messagebox.showerror("Error", str(e))

def delete_reader():
    try:
        conn = get_connection()
        cursor = conn.cursor()
        sql = "DELETE FROM Readers WHERE ReaderID=?"
        cursor.execute(sql, (entry_8.get(),))
        conn.commit()
        cursor.close()
        conn.close()
        messagebox.showinfo("Success", "Reader deleted successfully!")
    except Exception as e:
        messagebox.showerror("Error", str(e))

def find_reader():
    try:
        conn = get_connection()
        cursor = conn.cursor()
        sql = "SELECT FullName, Phone, Address FROM Readers WHERE ReaderID=?"
        cursor.execute(sql, (entry_8.get(),))
        row = cursor.fetchone()
        cursor.close()
        conn.close()
        if row:
            entry_2.delete(0, 'end'); entry_2.insert(0, row[0])
            entry_3.delete(0, 'end'); entry_3.insert(0, row[1])
            entry_4.delete(0, 'end'); entry_4.insert(0, row[2])
        else:
            messagebox.showinfo("Not found", "Reader ID not found")
    except Exception as e:
        messagebox.showerror("Error", str(e))

